import { ref, computed } from 'vue';
import axios from 'axios';
import { VaInput, VaSelect, VaDataTable, VaAlert, VaChip, VaCard } from 'vuestic-ui';
import { useI18n } from 'vue-i18n';
export default {
  __name: 'TeamDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const {
      t
    } = useI18n();
    const groups = ref([]);
    const filter = ref("");
    const filterByFields = ref([]);
    const filteredCount = ref(null);
    const headerStyle = ref({
      fontSize: '20px',
      // Ajusta el tamaño de la fuente según tus necesidades
      fontWeight: 'bold',
      // Opcional: para hacer el texto en negrita
      backgroundColor: '#cf0492' // Cambia el color del fondo del encabezado si es necesario
    });
    const fetchGroups = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/grupos');
        groups.value = response.data;
      } catch (error) {
        console.error('Error fetching groups:', error);
      }
    };
    const downloadCSV = async () => {
      try {
        let csvContent = 'data:text/csv;charset=utf-8,';
        groups.value.forEach((group, groupIndex) => {
          csvContent += `Grupo ${groupIndex + 1}\n`;
          csvContent += 'Nombre,Apellido,Email,Front,Back,Bootcamp\n';
          group.forEach(person => {
            csvContent += `${person.nombre},${person.apellido},${person.email},${person.front},${person.back},${person.bootcamp}\n`;
          });
          csvContent += '\n';
        });
        const encodedUri = encodeURI(csvContent);
        const link = document.createElement('a');
        link.setAttribute('href', encodedUri);
        link.setAttribute('download', 'grupos.csv');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      } catch (error) {
        console.error('Error al descargar el archivo CSV:', error);
      }
    };
    const flattenedGroups = computed(() => {
      return groups.value.flatMap((group, groupIndex) => {
        return group.map(person => ({
          ...person,
          groupIndex: groupIndex + 1
        }));
      });
    });
    const filteredGroupsByCard = computed(() => {
      return groups.value.map(group => {
        return group.filter(person => customFilteringFn(person, {
          column: {
            key: filterByFields.value.join()
          }
        }));
      });
    });
    const columns = [{
      key: "groupIndex",
      label: "table.group"
    }, {
      key: "nombre",
      label: "table.name"
    }, {
      key: "apellido",
      label: "table.surname"
    }, {
      key: "email",
      label: "table.email"
    }, {
      key: "front",
      label: "table.front"
    }, {
      key: "back",
      label: "table.back"
    }, {
      key: "bootcamp",
      label: "table.bootcamp"
    }];
    const translatedColumns = computed(() => {
      return columns.map(column => ({
        ...column,
        label: t(column.label)
      }));
    });
    const columnsWithName = [{
      value: "groupIndex",
      text: t("table.group")
    }, {
      value: "nombre",
      text: t("table.name")
    }, {
      value: "apellido",
      text: t("table.surname")
    }, {
      value: "email",
      text: t("table.email")
    }, {
      value: "front",
      text: t("table.front")
    }, {
      value: "back",
      text: t("table.back")
    }, {
      value: "bootcamp",
      text: t("table.bootcamp")
    }];
    const customFilteringFn = (source, cellData) => {
      if (!filter.value) {
        return true;
      }
      if (filterByFields.value.length >= 1) {
        const searchInCurrentRow = filterByFields.value.some(field => cellData.column.key === field);
        if (!searchInCurrentRow) return false;
      }
      const filterRegex = new RegExp(filter.value, "i");
      return filterRegex.test(source);
    };
    const __returned__ = {
      t,
      groups,
      filter,
      filterByFields,
      filteredCount,
      headerStyle,
      fetchGroups,
      downloadCSV,
      flattenedGroups,
      filteredGroupsByCard,
      columns,
      translatedColumns,
      columnsWithName,
      customFilteringFn,
      ref,
      computed,
      get axios() {
        return axios;
      },
      get VaInput() {
        return VaInput;
      },
      get VaSelect() {
        return VaSelect;
      },
      get VaDataTable() {
        return VaDataTable;
      },
      get VaAlert() {
        return VaAlert;
      },
      get VaChip() {
        return VaChip;
      },
      get VaCard() {
        return VaCard;
      },
      get useI18n() {
        return useI18n;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
