// src/i18n.js
import { createI18n } from 'vue-i18n';

// Mensajes de ejemplo
const messages = {
  en: {
    message: {
      home: 'home'
    }
  },
  es: {
    message: {
      inicio: 'inicio'
    }
  }
};

// Crear la instancia de i18n
const i18n = createI18n({
  locale: 'en', // Idioma por defecto
  messages,
});

export default i18n;
